import React from 'react';
import '../App.css';


export default function CurrentWeather({ weather, home }){
    const stormy = () => {
        console.log(weather)
        return weather.weather[0].main === "Thunderstorm" 
            ? <h3>Risky weather is coming your way!</h3>
            : <h3>The risks are low right now!</h3>
    }

    if (!home) {
        return(
            <div className="weather-info">
                {stormy()}
                <h4 className="city" >{weather["name"]}</h4>
                <div className="details">
                    <p>{weather.weather[0].main}</p>
                    <img src="https://openweathermap.org/img/wn/{weather.weather[0].icon}" />
                    <p>Description: {weather.weather[0].description}</p>
                    <p>{weather.main.temp}<span>&#176;</span> F</p>
                    <p>Feels Like {weather.main.feels_like}<span>&#176;</span> F</p>
                    <p>High: {weather.main.temp_max}<span>&#176;</span> F</p>
                    <p>Low: {weather.main.temp_min}<span>&#176;</span> F</p>
                    <p>Humidity: {weather.main.humidity}</p>
                    <p>Wind: {weather.wind.speed} MPH</p>
                </div>
            </div>
        )
    } else {
        return null
    }
}
